import {useEffect, useState} from "react";
import{useNavigate, useParams} from "react-router-dom"
import http from "../http";
const Edit =(props)=>{
    const navigate =useNavigate();
    const [name,setname]=useState();
    const [email,setemail]=useState();
    const [pass,setpass]=useState(); 
    const {id}=useParams;

    useEffect(()=>{
        fetchUser();
    },[]);

  const fetchUser =()=>{
      http.get("/users/"+id+"/edit").then((res)=>{
            
              name=res.data.name
              email=res.data.email
            
           

      })
  }  
     
const submitForm =()=>{
    var obj ={
        name:name,
        email:email,
        password:pass
    }
    http.post("/users",obj).then((res)=>{
        navigate("/");
    })
   

}

return(
    <div>
    <h1>Create User</h1>
     <div className="card"> 
      <div className="card-body">
        <input type="text" className="form-control m-2" placeholder="Entere Name"
           onChange={e => setname(e.target.value)}
           value={name}
        /> 
        <input type="email" className="form-control m-2" placeholder="Entere Email"
           onChange={e => setemail(e.target.value)} 
           value={email}
        /> 
        <input type="password" className="form-control m-2" placeholder="**********"
           onChange={e => setpass(e.target.value)}
        /> 
        <input type="button"   className="btn btn-success m-2" value="Create" /> 



      </div>

     </div>

    </div>
)


}

export default Edit;
